     for (nd=0;nd<MAX_ARRAY_DIMENSION;nd++)
	ghost_cell_width[nd] = this->Array_Descriptor.Array_Domain.InternalGhostCellWidth[nd];
     // ... (10/25/96,kdb) turn the following off for now because
